import"https://unpkg.com/mermaid@10/dist/mermaid.min.js";document.addEventListener("DOMContentLoaded",function(){console.log("Initializing Mermaid..."),mermaid.initialize({startOnLoad:!0,theme:"default",securityLevel:"loose"});const s=document.querySelectorAll("pre code"),i=[];s.forEach(t=>{const e=t.textContent.trim();(e.startsWith("graph ")||e.startsWith("sequenceDiagram")||e.startsWith("flowchart ")||e.startsWith("gantt")||e.startsWith("pie ")||e.includes("graph TB")||e.includes("graph LR"))&&i.push(t)}),console.log("Found",i.length,"mermaid code blocks"),i.forEach((t,e)=>{const o=t.textContent.trim();console.log("Converting block",e,":",o.substring(0,30)+"...");const n=document.createElement("div");n.className="mermaid",n.textContent=o,n.style.textAlign="center",n.style.margin="2rem 0";const r=t.closest("pre");r.parentNode.replaceChild(n,r)}),setTimeout(()=>{console.log("Re-initializing Mermaid after block conversion..."),mermaid.init()},100)});
